CREATE TABLE IF NOT EXISTS decks_questions (deck_question_id INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, current_cards integer ARRAY DEFAULT ARRAY[], discarded_cards integer ARRAY DEFAULT ARRAY[]);
CREATE TABLE IF NOT EXISTS info_players (player_id INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, name varchar(256) not null, score integer not null, color integer not null);
CREATE TABLE IF NOT EXISTS boards (board_id INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, size integer not null, symbols varchar(1024) not null);
CREATE TABLE IF NOT EXISTS game_sessions (game_id INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, nb_faces integer not null, board_id integer references boards (board_id), deck_question_id integer references decks_questions (deck_question_id));
CREATE TABLE IF NOT EXISTS game_players (game_id integer references game_sessions(game_id), player_id integer references info_players(player_id), position integer not null, primary key (game_id, player_id));